mapper.select:
SELECT * FROM (
	SELECT Tb.*, rownum rNum FROM (
		SELECT * FROM LECTURE

			WHERE ${searchField} LIKE '%'||#{searchKeyword}||'%'

		ORDER BY lecture_id DESC
	) Tb
)
WHERE rNum>=#{start} AND rNum<=#{end}

SELECT * FROM (
	SELECT Tb.*, rownum rNum FROM (
		SELECT * FROM myboard

			WHERE ${searchField} LIKE '%'||#{searchKeyword}||'%'

		ORDER BY idx DESC
	) Tb
)
WHERE rNum>=#{start} AND rNum<=#{end}

SELECT * FROM (
	SELECT Tb.*, rownum rNum FROM (
		SELECT * FROM ABSENTS

			WHERE ${searchField} LIKE '%'||#{searchKeyword}||'%'

		ORDER BY USER_ID ASC
	) Tb
)
WHERE rNum>=#{start} AND rNum<=#{end}
